import { useState } from "react";
import styled from "styled-components";

function eventHandler(e) {
  console.log(e.target.value);
}
export const Slider = () => {
  return (
    <Body>
      <div className="range">
        <div className="sliderValue">
          <span>100</span>
        </div>
        <div className="field">
          <div className="value left">0</div>
          <input
            type="range"
            min="0"
            max="100"
            steps="1"
            onChange={eventHandler}
          ></input>
          <div className="value right">100</div>
        </div>
      </div>
    </Body>
  );
};

export const Body = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #664aff;
  .range {
    height: 80px;
    width: 380px;
    background-color: #fff;
    border-radius: 10px;
  }
  .field {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
  }
  .range .field {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
  }
  .range .field input {
    -webkit-appearance: none;
    height: 3px;
    width: 100%;
    background-color: #ddd;
    border-radius: 5px;
    outline: none;
    border: none;
  }
`;
