import { useState } from "react";
import styled from "styled-components";

function eventHandler(e) {
  console.log(e.target.value);
  setValut(e.target.value);
}
export const Slider = () => {
  const [value, setValut] = useState(50);
  return (
    <Body>
      <div className="range">
        <div className="sliderValue">
          <span>{value}</span>
        </div>
        <div className="field">
          <div className="value left">0</div>
          <input
            type="range"
            min="0"
            max="100"
            steps="1"
            onChange={eventHandler}
          ></input>
          <div className="value right">100</div>
        </div>
      </div>
    </Body>
  );
};

export const Body = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #664aff;
  .range {
    height: 80px;
    width: 380px;
    background-color: #fff;
    border-radius: 10px;
    padding: 0 65px 0 45px;
  }
  .range .sliderValue {
    position: relative;
    width: 100%;
  }
  .range .sliderValue span {
    position: absolute;
    height: 45px;
    width: 45px;
    /* background-color: #664aff; */
    color: #fff;
    font-weight: 500;
    top: -40px;
    transform: translateX(-50%);
    line-height: 55px;
    z-index: 2;
  }
  .range .sliderValue span:after {
    position: absolute;
    content: "";
    height: 45px;
    width: 45px;
    background: #664aff;
    left: 50%;
    transform: translateX(-50%) rotate(45deg);
    border: 3px solid #fff;
    z-index: -1;
    border-top-left-radius: 50%;
    border-top-right-radius: 50%;
    border-bottom-left-radius: 50%;
  }

  .field {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
  }
  .range .field {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100%;
  }
  .range .field .value {
    position: absolute;
    font-size: 18px;
    font-weight: 600;
    color: #664aff;
  }
  .range .field .value.left {
    left: -22px;
  }
  .range .field .value.right {
    right: -43px;
  }
  .range .field input {
    -webkit-appearance: none;
    height: 3px;
    width: 100%;
    background-color: #ddd;
    border-radius: 5px;
    outline: none;
    border: none;
  }
  .range .field input::-webkit-slider-thumb {
    -webkit-appearance: none;
    height: 20px;
    width: 20px;
    background-color: #664aff;
    border-radius: 50%;
    border: 1px solid #664aff;
    cursor: pointer;
  }
`;
